import React, { useState } from "react";
import BirthdayList from "./BirthdayList";
import NewBirthday from "../NewBirthdayCard/NewBirthday";
import BirthdayFilter from "./BirthdayFilter.js";

const birthday = [
  {
    name: "Rawan Abdullah",
    date: new Date(2000, 8, 11),
  },
  {
    name: "Alaa Abdullah",
    date: new Date(1999, 2, 25),
  },
  {
    name: "Mohammad Abdullah",
    date: new Date(2004, 6, 21),
  },
  {
    name: "Jood Abdullah",
    date: new Date(2015, 1, 5),
  },
  {
    name: "Radwan Abdullah",
    date: new Date(1995, 11, 31),
  },
  {
    name: "soha fadel",
    date: new Date(1976, 3, 21),
  },
];

const [filteredYear, setFilteredYear] = useState('January');

const filterChangeHandler=(filterYear)=>{
  setFilteredYear(filterYear);
}
const Birthday = () => {
  const [birthdayCard, setBirthdayCard] = useState(birthday);
  const AddBirthdayCard = (newbirthday) => {
    setBirthdayCard((prevBirthday) => {
      return [...prevBirthday, newbirthday];
    });
  };
  return (
    <div>
      <NewBirthday onAddBirthdayCard={AddBirthdayCard} />
      <BirthdayList birthdayCard={birthdayCard} />
    </div>
  );
};

export default Birthday;
